import stringfrom agent import *from models.products import *import redebug = Truedef run(context, session):     session.queue(Request('http://www.hi-fi.com.pl/'), process_frontpage, {})def process_frontpage(data, context, session):    for cat in data.xpath("//a[regexp:test(., 'Testy')]/following-sibling::div//a"):        context['category'] = cat.xpath(".//text()[string-length(normalize-space(.))>0]").string()        cat_url = cat.xpath("@href").string()        if context['category'] and cat_url:            session.queue(Request(cat_url), process_category, context)def process_category(data, context, session):    for p in data.xpath("//div[@class='article-head']/h2/a"):        context['name'] = p.xpath(".//text()[string-length(normalize-space(.))>0]").string()        context['url'] = p.xpath("@href").string()        if context['name'] and context['url']:            session.queue(Request(context['url']), process_product, context)    next = data.xpath("//li[@class='pagination-next']/a/@href").string()    if next:        session.queue(Request(next), process_category, context)def process_product(data, context, session):    product = Product()    product.name = context['name']    product.url = context['url']    product.category = Category(name = context['category'])    product.ssid = re_search_once('\/(\d+)', product.url)    review = Review()    review.ssid = product.ssid    review.url = product.url    review.type = 'pro'    date = data.xpath("//p/text()[regexp:test(., 'Źródło\:')]").string()    if date:        review.date = re_search_once('\:(.+)', date)    excerpt = data.xpath("//div[@class='item-page']/p[.//text()[string-length(normalize-space(.))>100]][1]//text()[string-length(normalize-space(.))>0]").string(multiple=True)    if excerpt:        review.properties.append(ReviewProperty(type=ReviewPropertyType(name='excerpt'), value=excerpt))    summary = data.xpath("//strong[regexp:test(., 'Konkluzja')]/following-sibling::text()[string-length(normalize-space(.))>0]").string(multiple=True)    if not summary:        summary = data.xpath("//p[.//strong[regexp:test(., 'Konkluzja')]]/text()[string-length(normalize-space(.))>0]").string(multiple=True)    if summary:        review.properties.append(ReviewProperty(type=ReviewPropertyType(name='summary'), value=summary, name = "Konkluzja"))    tuser = data.xpath("//p/text()[regexp:test(., 'Autor\:')]").string()    if tuser:        user = re_search_once('\:(.+)', tuser)        if user:            review.authors.append(Person(name = user, ssid = user))    product.reviews.append(review)    session.emit(product)