import stringfrom agent import *from models.products import *import redebug = Truedef run(context, session):     session.queue(Request('http://www.gamesunit.de/tag/Review'), process_frontpage, {})def process_frontpage(data, context, session):    for p in data.xpath("//div[@id='pageContentLeft']//h1/a"):        context['name'] = p.xpath(".//text()[string-length(normalize-space(.))>0]").string()        context['url'] = p.xpath("@href").string()        if context['name'] and context['url']:            session.queue(Request(context['url']), process_product, context)def process_product(data, context, session):    product = Product()    review = Review()    product.name = context['name']    review.url = product.url = context['url']    product.category = Category(name = "Games")    review.ssid = product.ssid = re_search_once('(\d+).htm', product.url)    review.title = context['name']    review.type = 'pro'    review.date = data.xpath("//h4/text()[string-length(normalize-space(.))>0]").string()    if review.date:        review.date = review.date.replace(', von', '')    user_data = data.xpath("//h4/a").first()    if user_data:        user = Person()        user.name = user_data.xpath(".//text()[string-length(normalize-space(.))>0]").string()        user.profile_url = user_data.xpath("@href").string()        user.ssid = re_search_once('autor\/(.+)\.htm', user.profile_url)        review.authors.append(user)    excerpt = data.xpath("//div[@id[regexp:test(., 'ctl00_content')]]/p[.//text()[string-length(normalize-space(.))>100]][1]//text()[string-length(normalize-space(.))>0]").string(multiple=True)    if excerpt:        review.properties.append(ReviewProperty(type=ReviewPropertyType(name='excerpt'), value=excerpt))    summary = data.xpath("//p[b[regexp:test(., 'Fazit')]][1]//text()[string-length(normalize-space(.))>0][not(parent::b[regexp:test(., 'Fazit')])]").string()    if summary:        review.properties.append(ReviewProperty(type=ReviewPropertyType(name='summary'), value=summary, name = "Fazir"))    product.reviews.append(review)    session.emit(product)