from agent import *from models.products import *import simplejsonXCAT = ['PC GROSBILL & CONFIG SUR MESURE', 'VENTES FLASH & BONS PLANS', 'PC reconditionnés', 'Composants reconditionnés', 'Stockages reconditionnés', 'Réseaux reconditionnés', 'Périphériques reconditionnés', 'Gaming reconditionné', 'Nos bons plans']def strip_namespace(data):    tmp = data.content_file + ".tmp"    out = file(tmp, "w")    for line in file(data.content_file):        line = line.replace('<ns0', '<')        line = line.replace('ns0:', '')        line = line.replace(' xmlns', ' abcde=')        out.write(line + "\n")    out.close()    os.rename(tmp, data.content_file)def run(context, session):    session.browser.use_new_parser = True    session.sessionbreakers = [SessionBreak(max_requests=3000)]    session.queue(Request('https://www.grosbill.com/'), process_frontpage, dict())def process_frontpage(data, context, session):    strip_namespace(data)    cats = data.xpath('//div[contains(@class, "menu-hori")]')    for cat in cats:        name = cat.xpath('label//p//text()').string(multiple=True).strip()        if name not in XCAT:            cats1 = cat.xpath('div[@class="sous_level"]//ul[contains(@class, "grb_menu")]')            for cat1 in cats1:                name1 = cat1.xpath('li[@class]//span/text()').string()                if name1 not in XCAT:                    cats2 = cat1.xpath('li[not(@class)]/a')                    for cat2 in cats2:                        name2 = cat2.xpath('span/text()').string()                        if 'tous les' in name2.lower():                            name2 = ''                        url = cat2.xpath('@href').string().strip('+')                        session.queue(Request(url), process_prodlist, dict(cat=name+'|'+name1+'|'+name2))def process_prodlist(data, context, session):    strip_namespace(data)    prods = data.xpath('//div[contains(@class, "ppp-")]') or data.xpath('//div[@id="lst-configs"]/div')    for prod in prods:        name = prod.xpath('.//p[contains(@class, "information__libelle__libelle")]/span/text()').string(multiple=True) or prod.xpath('.//div[contains(@class, "product-title")]/p//text()').string(multiple=True)        url = prod.xpath('a/@href').string().replace('%09', '')        revs_cnt = prod.xpath('.//div[contains(@class, "nbr_avis")]/p/text()').string() or prod.xpath('//div[contains(@class, "list__rating")]/div[not(@class)]/text()').string()        if revs_cnt:            revs_cnt = revs_cnt.split('avis')[0].strip('( )')            if revs_cnt and int(revs_cnt) > 0:                session.queue(Request(url), process_product, dict(context, name=name.strip(), url=url))    next_url = data.xpath('//link[@rel="next"]/@href').string()    if next_url:        session.queue(Request(next_url), process_prodlist, dict(context))def process_product(data, context, session):    strip_namespace(data)    product = Product()    product.name = context['name']    product.url = context['url']    product.category = context['cat'].rstrip('|')    product.manufacturer = data.xpath('//meta[@property="product:brand"]/@content').string()    product.ssid = product.url.split('-')[-1].split('.')[0]    product.sku = data.xpath('//p[contains(@id, "lbl_num_produit")]/text()').string()    ean = data.xpath('//p[contains(@id, "code_ean")]/text()').string()    if ean:        product.properties.append(ProductProperty(type='id.ean', value=ean))    mpn = data.xpath('//p[contains(@id, "lbl_ref_constructeur")]/text()').string()    if mpn:        mpn = mpn.rstrip('* ')        product.properties.append(ProductProperty(type='id.manufacturer', value=mpn))    revs = data.xpath('//div[contains(@class, "page__review-item")]')    for rev in revs:        review = Review()        review.type = 'user'        review.url = product.url        author = rev.xpath('.//p[@class="un_avis__nom"]/text()').string()        if author and author.strip():            review.authors.append(Person(name=author, ssid=author))        grade_overall = rev.xpath('.//span[contains(@class, "note")]/text()').string()        if grade_overall:            grade_overall = float(grade_overall.split('/')[0].strip('( )'))            review.grades.append(Grade(type='overall', value=grade_overall, best=5.0))        excerpt = rev.xpath('.//p[contains(@class, "commentaire")]/text()').string(multiple=True)        if excerpt:            excerpt = excerpt.strip(' .?"')            if excerpt:                review.add_property(type='excerpt', value=excerpt)                review.ssid = review.digest() if author else review.digest(excerpt)                product.reviews.append(review)    if product.reviews:        session.emit(product)    # Loaded all revs