from agent import *from models.products import *def run(context, session):    session.browser.use_new_parser = True    session.queue(Request("https://www.clearchemist.co.uk/"), process_frontpage, dict())def process_frontpage(data, context, session):    cats1 = data.xpath('//div[contains(@class, "level0")]')    for cat1 in cats1:        name1 = cat1.xpath('a/span/text()').string()        if name1 == 'Shop':            name1 = ''        cats2 = cat1.xpath('.//div[contains(@class, "cat-level-3")]')        for cat2 in cats2:            name2 = cat2.xpath('a/span/text()').string()            url = cat2.xpath('a/@href').string()            cats3 = cat2.xpath('.//div[contains(@class, "cat-level-4")]')            if not cats3:                if '?' in url:                    url += '&product_list_limit=40'                else:                    url += '?product_list_limit=40'                session.queue(Request(url), process_prodlist, dict(cat=name1+'|'+name2))            for cat3 in cats3:                name3 = cat3.xpath('a/span/text()').string()                url = cat3.xpath("a/@href").string()                cats4 = cat3.xpath('.//div[contains(@class, "cat-level-5")]')                if not cats4:                    if '?' in url:                        url += '&product_list_limit=40'                    else:                        url += '?product_list_limit=40'                    session.queue(Request(url), process_prodlist, dict(cat=name1+'|'+name2+'|'+name3))                for cat4 in cats4:                    name4 = cat4.xpath('a/span/text()').string()                    url = cat4.xpath('a/@href').string()                    if '?' in url:                        url += '&product_list_limit=40'                    else:                        url += '?product_list_limit=40'                    session.queue(Request(url), process_prodlist, dict(cat=name1+'|'+name2+'|'+name3+'|'+name4))def process_prodlist(data, context, session):    prods = data.xpath('//li[contains(@class, "item product")]')    for prod in prods:        name = prod.xpath('.//a[@class="product-item-link"]/text()').string()        url = prod.xpath('.//a[@class="product-item-link"]/@href').string()        ssid = prod.xpath('.//div/@data-product-id').string()        sku = prod.xpath('.//form/@data-product-sku').string()        revs_cnt = prod.xpath('.//a[@class="action view"]/text()').string()        if revs_cnt and int(revs_cnt) > 0:            revs_url = "https://www.clearchemist.co.uk/review/product/listAjax/id/{}/".format(ssid)            session.queue(Request(revs_url), process_reviews, dict(context, name=name, url=url, ssid=ssid, sku=sku))    next_url = data.xpath('//a[@rel="next"]/@href').string()    if next_url:        session.queue(Request(next_url), process_prodlist, dict(context))def process_reviews(data, context, session):    product = Product()    product.name = context['name']    product.url = context['url']    product.category = context['cat'].strip(' |')    product.ssid = context['ssid']    sku = context.get('sku')    if sku and sku.isdigit() == True and 11 < len(sku) < 15:        product.properties.append(ProductProperty(type='id.ean', value=sku))    elif sku:        product.sku = sku    revs = data.xpath("//li[@class='item review-item']")    for rev in revs:        review = Review()        review.title = rev.xpath('div[@class="review-title"]/text()').string()        review.url = product.url        review.type = 'user'        review.date = rev.xpath('.//time/@datetime').string()        author = rev.xpath('.//strong[@itemprop="author"]/text()').string()        if author:            review.authors.append(Person(name=author, ssid=author))        grade_overall = rev.xpath('.//span[@itemprop="ratingValue"]/text()').string()        if grade_overall:            value = float(grade_overall.strip("%")) / 20            review.grades.append(Grade(type='overall', value=float(value), best=5.0))        excerpt = rev.xpath(".//div[@class='review-content']//text()").string(multiple=True)        if excerpt:            review.properties.append(ReviewProperty(type='excerpt', value=excerpt))            review.ssid = review.digest() if author else review.digest(excerpt)            product.reviews.append(review)    if product.reviews:        session.emit(product)    # No next_url