from agent import *from models.products import *import simplejsonX_CATS = ['FINANCE', 'SPECIAL OFFERS', 'EX-DISPLAY / PRE-LOVED', 'FIND BY PART', ]NO_SUBCATS = ['Sewing Machines', 'Overlockers', 'Ironing Presses']def run(context, session):    session.sessionbreakers = [SessionBreak(max_requests=4000)]    session.queue(Request("https://coulingsewingmachines.co.uk/"), process_catlist, dict())def process_catlist(data, context, session):    cats = data.xpath('//ul[@class="navPages-list"]/li')    for cat in cats:        name = cat.xpath('a//text()').string(multiple=True)        url = cat.xpath('a/@href').string()        if name not in X_CATS:            cats1 = cat.xpath('(.//ul[@class="navPage-subMenu-list"]/li)[position() > 1]')            if cats1 and name not in NO_SUBCATS:                for cat1 in cats1:                    cat1_name = cat1.xpath('a//text()').string(multiple=True)                    url = cat1.xpath('a/@href').string()                    if cat1_name not in X_CATS:                        subcats = cat1.xpath('ul[@class="navPage-childList"]/li/a')                        if subcats:                            for subcat in subcats:                                subcat_name = subcat.xpath('.//text()').string(multiple=True)                                url = subcat.xpath('@href').string()                                session.queue(Request(url), process_prodlist, dict(cat=name + "|" + cat1_name + "|" + subcat_name))                        else:                            session.queue(Request(url), process_prodlist, dict(cat=name + "|" + cat1_name))            else:                session.queue(Request(url), process_prodlist, dict(cat=name))def process_prodlist(data, context, session):    prods = data.xpath('//li[@class="product"]')    for prod in prods:        name = prod.xpath('.//h4[@class="listItem-title"]/a//text()').string()        url = prod.xpath('.//h4[@class="listItem-title"]/a/@href').string()        ssid = prod.xpath('article/@data-entity-id').string()        brand = prod.xpath('article/@data-product-brand').string() or prod.xpath('.//p[@class="listItem-brand"]//text()').string()        session.queue(Request(url), process_product, dict(context, name=name, url=url, ssid=ssid, brand=brand))    next_url = data.xpath('//link[@rel="next"]/@href').string()    if next_url:        session.queue(Request(next_url), process_prodlist, dict(context))def process_product(data, context, session):    product = Product()    product.name = context['name']    product.category = context['cat']    product.url = context['url']    product.manufacturer = context['brand']    product.ssid = context['ssid']    mpn = data.xpath('//meta[@itemprop="mpn"]/@content').string()    if mpn and mpn.lower() in product.url:        product.add_property(type='id.manufacturer', value=mpn)    ean = data.xpath('//dd[@data-product-upc="data-product-upc"]//text()').string()    if ean and ean.isdigit() == True:        product.add_property(type='id.ean', value=ean)    # revs_cnt = data.xpath('//a[@id="productReview_link"]//text()').string()    # if revs_cnt:    #     revs_cnt = revs_cnt.split(' review')[0].lstrip('(')    revs = data.xpath('//ul[li[@class="productReview"]]')    for rev in revs:        review = Review()        review.title = rev.xpath('li/h5[@class="productReview-title"]//text()').string(multiple=True)        review.type = 'user'        review.url = product.url        date = rev.xpath('(following::body/p[@class="productReview-author"]//text())[1]').string()        if date:            review.date = date.split(' on ')[-1]        author = rev.xpath('(following::head/meta[@itemprop="author"]/@content)[1]').string()        if author:            review.authors.append(Person(name=author, ssid=author))        grade_overall = rev.xpath('li//span[@itemprop="ratingValue"]//text()').string()        if grade_overall:            review.grades.append(Grade(type='overall', value=float(grade_overall), best=5.0))        excerpt = rev.xpath('(following::body/p[@class="productReview-body"]//text())[1]').string(multiple=True)        if excerpt:            review.add_property(type='excerpt', value=excerpt)            review.ssid = review.digest() if author else review.digest(excerpt)            product.reviews.append(review)    if product.reviews:        session.emit(product)    # No next page, no more than 10 revs