from agent import *from models.products import *def process_prodlist(data, context, session):    prods = data.xpath("//div[@id='main']//div[h3]")    for prod in prods:        name = prod.xpath(".//h3//text()").string()        url = prod.xpath(".//a//@href").string()        session.queue(Request(url, use='curl'), process_product, dict(context, name=name, url=url))def process_product(data, context, session):    product = Product()    product.name = context['name'].split(" Review")[0].split(" review")[0].split("â€“")[0]    product.ssid = context['url'].split('/')[-2]    product.url = context['url']    product.category = 'Lighting'    review = Review()    review.title = context['name']    review.date = data.xpath("//span[@class='post-date updated']//@datetime").string()    review.ssid = product.ssid    review.type = 'pro'    review.url = context['url']        author_name = data.xpath("//h5//span[@itemprop='author']//span[@itemprop='name']//text()").string()    author_url = data.xpath("//h5//span[@itemprop='author']/a[@rel='author']//@href").string()    if author_name:        if author_url:            review.authors.append(Person(name=author_name, profile_url=author_url, ssid=author_name))        else:            review.authors.append(Person(name=author_name, ssid=author_name))        summary = data.xpath("//span[@itemprop='description']//text()").string(multiple=True)    if not summary:        summary = data.xpath("//div[@itemprop='articleBody']//p[1]//text()").string()    if summary:        review.properties.append(ReviewProperty(type='summary', value=summary))    conclusion = data.xpath("//h3[text()[contains(.,'Verdict')]]//following-sibling::p//text()").string(multiple=True)    if not conclusion:        conclusion = data.xpath("//h3[text()[contains(.,'Overall')]]//following-sibling::p[1]//text()").string(multiple=True)    if conclusion:        review.properties.append(ReviewProperty(type='conclusion', value=conclusion))        grade_overall = data.xpath("//meta[@itemprop='ratingValue']//@content").string()    if grade_overall:        review.grades.append(Grade(type='overall', value=float(grade_overall), best=5.0))        pros = data.xpath("//h4[text()[contains(., 'Pros')]]//following-sibling::ul[1]/li")    for pro in pros:        pro = pro.xpath("text()").string().replace('+', '')        review.add_property(type='pros', value=pro)        cons = data.xpath("//h4[text()[contains(., 'Cons')]]//following-sibling::ul[1]/li")    for con in cons:        con = con.xpath("text()").string().replace('-', '')        review.add_property(type='cons', value=con)        excerpt = data.xpath("//div[@itemprop='reviewBody']//p//text()").string(multiple=True)    if not excerpt:        excerpt = data.xpath("//div[@itemprop='articleBody']//p//text()").string(multiple=True)    if excerpt:        if summary:            excerpt = excerpt.replace(summary,'')        if conclusion:            excerpt = excerpt.replace(conclusion,'')        review.properties.append(ReviewProperty(type='excerpt', value=excerpt.strip()))        product.reviews.append(review)        session.emit(product)def run(context, session):    session.queue(Request("https://huehomelighting.com/home-lighting-reviews/", use='curl'), process_prodlist, dict())